
window.onload = function() {
  const layout = [
    { key: 'z', top: '70%', left: '10%' },
    { key: 'r', top: '70%', left: '30%' },
    { key: 'd', top: '70%', left: '50%' },
    { key: 'f', top: '70%', left: '70%' },
    { key: 'g', top: '70%', left: '90%' }
  ];

  layout.forEach(({ key, top, left }) => {
    const btn = document.createElement('button');
    btn.innerText = key.toUpperCase();
    btn.className = 'gc-touch';
    btn.style.top = top;
    btn.style.left = left;
    btn.onclick = () => {
      const e = new KeyboardEvent('keydown', { key: key });
      window.dispatchEvent(e);
    };
    document.body.appendChild(btn);
  });
};
